import React, { Component } from "react";
import ReactDOM from "react-dom";
import Grid from "../Grid";

class GridSize extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numRows: 0,
      numCols: 0,
    };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  handleSubmit() {
    document.getElementById("input_size").style.display = "none";
    // ReactDOM.render(
    //   <Grid numRows={this.state.numRows} numCols={this.state.numCols} />,
    //   document.getElementById("grid")
    // );
    console.log("Grid element rendered");
  }
  render() {
    return (
      <div id="container">
        <form id="input_size" onSubmit={this.handleSubmit}>
          <label>
            Enter Number of Rows:
            <input
              name="numRows"
              type="number"
              value={this.state.numRows}
              onChange={this.handleInputChange}
            />
          </label>
          <br />
          <label>
            Enter Number of Columns:
            <input
              name="numCols"
              type="number"
              value={this.state.numCols}
              onChange={this.handleInputChange}
            />
          </label>
          <br />
          <input type="submit" value="Submit" />
        </form>
      </div>
    );
  }
}

export default GridSize;
