import React, { Component } from "react";
import Box from "../Box/Box";

import "./Grid.css";
class Grid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numRows: 0,
      numCols: 0,
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }
  fillTable() {
    for (var row = 0; row < this.state.numRows; row++) {
      var ele = document.createElement("tr");
      ele.id = row;
      for (var col = 0; col < this.state.numCols; col++) {
        var temp = document.createElement("td");
        temp.innerHTML = "<Box />";
        temp.id = "" + row + "-" + col;
        ele.appendChild(temp);
      }
      document.getElementById("grid").firstChild.appendChild(ele);
    }
  }
  handleInputChange(e){
    this.setState({
      [e.target.name]:e.target.value
    });
    console.log(e.target.value);
  }
  render() {
    return (
      <div>
        <form>
          <label>
            Enter Number of Rows:
            <input name="rows" type="number" value={this.state.numRows} onChange={this.handleInputChange}/>
          </label>
          <br />
          <label>
            Enter Number of Columns:
            <input name="cols" type="number" value={this.state.numCols} onChange={this.handleInputChange}/>
            </label>
        </form>
        <div>
          <table id="grid">
            <tbody></tbody>
          </table>
        </div>
      </div>
    );
  }

  PrintEle(ele) {
    console.log(ele);
  }
}

export default Grid;
