import React, { Component } from "react";
import Box from "../Box/Box";

import GridSize from "./GridSize/GridSize";

import "./Grid.css";
class Grid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numRows: 0,
      numCols: 0,
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }
  fillTable() {
    for (var row = 0; row < this.state.numRows; row++) {
      var ele = document.createElement("tr");
      ele.id = row;
      for (var col = 0; col < this.state.numCols; col++) {
        var temp = document.createElement("td");
        temp.innerHTML = "<Box />";
        temp.id = "" + row + "-" + col;
        ele.appendChild(temp);
      }
      document.getElementById("grid").firstChild.appendChild(ele);
    }
  }
  handleInputChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  render() {
    return (
      <div>
        <GridSize />
        <table id="grid">
          <tbody></tbody>
        </table>
      </div>
    );
  }

  PrintEle(ele) {
    console.log(ele);
  }
}

export default Grid;
