import React, { Component } from "react";
import ReactDOM from "react-dom";

class Row extends Component {
  constructor(props) {
    super(props);
    this.r = document.createElement("tr");
  }

  fillRow() {
    const vw = Math.max(
      document.documentElement.clientWidth || 0,
      window.innerWidth || 0
    );
    document.getElementById("grid-table").child.appendChild(r);
    for (var rw = 0; rw < Math.trunc(vw / 20); rw++) {
        ReactDOM.createPortal(this.props.children, this.r);
    }
  }

  render() {
    return null;
  }
}

export default Row;
