import React, { Component } from "react";
import "./Box.css";
class Box extends Component {
  constructor(props) {
    super(props);
    this.state = { isToggleOn: true };
    this.handleClick = this.handleClick.bind(this);
    this.className = "sq";
  }
  handleClick() {
    this.setState((state) => ({ isToggleOn: !state.isToggleOn }));
    console.log("button is clicked");
    if (!this.className.includes("visited")) {
      this.className += " visited";
    }
  }

  changeBackground(e) {
    if (document.getElementById("BuildWalls").checked && !document.getElementById("DestroyWalls").checked) {
      e.target.style.background = "green";
      console.log("The Build Walls Button has been clicked");
    }
    else if(!document.getElementById("BuildWalls").checked && document.getElementById("DestroyWalls").checked){
      e.target.style.background = "blue";
      console.log("The Destroy Walls Button has been clicked");
    }
    else{
      console.log("No Button has been clicked");
    }
  }
  render() {
    return (
      <div>
        className={this.className}
        onClick={this.handleClick}
        onMouseEnter={this.changeBackground}
      ></div>
    );
  }
}

export default Box;
