import React, { useState, Component } from "react";
import {
  Navbar,
  Nav,
  NavDropdown,
  ButtonGroup,
  ToggleButton,
  Button,
} from "react-bootstrap";
class Bar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      rows: props.rows === undefined ? 0 : props.rows,
      cols: props.cols === undefined ? 0 : props.cols,
      checked: false,
    };
  }
  render() {
    var checked = this.state.checked;
    return (
      <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
        <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#features">Features</Nav.Link>
            <Nav.Link href="#pricing">Pricing</Nav.Link>
            <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">
                Separated link
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
          <Nav>
            <ButtonGroup toggle className="mb-2">
              <Button variant="danger" size="sm" id="reset" onClick={this.reset}>
                Reset Board
              </Button>
              <ToggleButton
                type="checkbox"
                variant="light"
                checked={!checked}
                onChange={(e) => {this.state.checked = !this.state.checked}}
                value="1"                
                id="BuildWalls"
              >
                Build Walls
              </ToggleButton>
              <ToggleButton
                type="checkbox"
                variant="light"
                checked={checked}
                value="1"
                onChange={(e) => {this.state.checked = !this.state.checked}}
                id="DestroyWalls"
              >
                Destroy Walls
              </ToggleButton>
            </ButtonGroup>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  }
  reset() {
    for (var r = 0; r < this.state.rows; r++) {
      for (var c = 0; c < this.state.cols; c++) {
        document.getElementById("" + r + "-" + c).style.background = "white";
      }
    }
  }
}
export default Bar;
