import React, { Component } from "react";

var rCount = 0;
var cCount = 0;

function getRowCount(){ return rCount; }
function getColCount() { return cCount; }

class GridSize extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numRows: 0,
      numCols: 0,
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
    if(e.target.name === "numRows"){
        rCount = e.target.value;
    }
    else{
        cCount = e.target.value;
    }
  }
  render() {
    return (
      <form>
        <label>
          Enter Number of Rows:
          <input
            name="numRows"
            type="number"
            value={this.state.numRows}
            onChange={this.handleInputChange}
          />
        </label>
        <br />
        <label>
          Enter Number of Columns:
          <input
            name="numCols"
            type="number"
            value={this.state.numCols}
            onChange={this.handleInputChange}
          />
        </label>
        <br/>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default GridSize
