import React, { Component } from "react";
import "./Box.css";
class Box extends Component {
  constructor(props) {
    super(props);
    this.backgroundColor = "white";
  }

  changeBackground(e) {

    var b = document.getElementById("BuildWalls");
    var d = document.getElementById("DestroyWalls");
    if(b.className.includes("active")){
      e.target.style.background = 'black';
    }
    else {
      e.target.style.background = 'white';
    }
    e.preventDefault();

  }

  handleOnClick(e) {
    if(e.target.background === 'black'){
      e.target.background = 'white';
    }
    else{
      e.target.background = 'black';
    }
  }
  render() {
    return (
      <div
        onDragOver={this.changeBackground}
        onDragOverCapture={(e) => {e.preventDefault()}}
        onClick={this.handleOnClick}
        id="box_ele"
      ></div>
    );
  }
}

export default Box;
