import React, { Component } from "react";
import Box from "../Box/Box";
import ReactDOM from "react-dom";
import "./Grid.css";
class Grid extends Component {
  constructor(props) {
    super(props);
    this.state = {
      numRows: props.numRows,
      numCols: props.numCols,
    };
  }
  fillTable() {
    for(var r = 0; r < this.state.numRows; r++){
      var ele = document.createElement("tr");
      ele.id = r;
      document.getElementById("grid").append(ele);
      for(var c = 0; c < this.state.numCols; c++){
        var c_ele = document.createElement("td");
        c_ele.id = r + "-" + c;
        document.getElementById(ele.id).append(c_ele);
        ReactDOM.render(<Box />, document.getElementById(c_ele.id));
      }
    }
  }

  render() {
    this.fillTable();
    return null;
  }

  PrintEle(ele) {
    console.log(ele);
  }
}

export default Grid;
