import React, { Component } from "react";
import ReactDOM from "react-dom"
import Grid from "../Grid";

class GridSize extends Component {
  constructor(props) {
    super(props);
    console.log("GridSize Element has been created");
    this.state = {
      numRows: 0,
      numCols: 0,
    };
    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }

  handleSubmit(e) {
    ReactDOM.render(<Grid numRows={this.state.numRows} numCols={this.state.numCols} />, document.getElementById("root"));
    alert("Submission Detected");
    e.preventDefault();
  }
  render() {
    return (
      <div id="container">
        <form id="input_size">
          <label>
            Enter Number of Rows:
            <input
              name="numRows"
              type="number"
              value={this.state.numRows}
              onChange={this.handleInputChange}
            />
          </label>
          <br />
          <label>
            Enter Number of Columns:
            <input
              name="numCols"
              type="number"
              value={this.state.numCols}
              onChange={this.handleInputChange}
            />
          </label>
          <br />
          <input type="submit" value="Submit" onSubmit={this.handleSubmit}/>
        </form>
      </div>
    );
  }
}

export default GridSize;
