import React, { Component } from "react";
import "./Box.css";
class Box extends Component {
  constructor(props) {
    super(props);
    this.state = { isToggleOn: true };
    this.handleClick = this.handleClick.bind(this);
    this.className = "sq";
  }
  handleClick() {
    this.setState((state) => ({ isToggleOn: !state.isToggleOn }));
    console.log("button is clicked");
    if (!this.className.includes("visited")) {
      this.className += " visited";
    }
  }

  changeBackground(e) {
    e.preventDefault();
    var b = document.getElementById("BuildWalls");
    var d = document.getElementById("DestroyWalls");
    if(b.className.includes("active")){
      e.target.style.background = 'black';
    }
    else {
      e.target.style.background = 'white';
    }
  }
  render() {
    return (
      <div
        className={this.className}
        onClick={this.handleClick}
        onMouseEnter={this.changeBackground}
      ></div>
    );
  }
}

export default Box;
